package com.hfy.log.entity;import java.util.Date;import com.fasterxml.jackson.annotation.JsonFormat;import com.hfy.base.entity.BaseEntity;public class Log extends BaseEntity {	private static final long serialVersionUID = 1L;	public Log() {		super();	}	/***/	private String ip;	public void setIp(String ip) {		this.ip = ip;	}	public String getIp() {		return this.ip;	}	/** 操作人 */	private String operPerson;	public void setOperPerson(String operPerson) {		this.operPerson = operPerson;	}	public String getOperPerson() {		return this.operPerson;	}	/** 操作类型 */	private String operType;	public void setOperType(String operType) {		this.operType = operType;	}	public String getOperType() {		return this.operType;	}	/** 操作标题 */	private String operTitle;	public void setOperTitle(String operTitle) {		this.operTitle = operTitle;	}	public String getOperTitle() {		return this.operTitle;	}	/**	 * 操作表 多表用，号分开	 */	private String operTable;	public void setOperTable(String operTable) {		this.operTable = operTable;	}	public String getOperTable() {		return this.operTable;	}	/** 操作请求 */	private String operUrl;	public void setOperUrl(String operUrl) {		this.operUrl = operUrl;	}	public String getOperUrl() {		return this.operUrl;	}	/** 操作开始时间 */	private Date operStartDate;	/** 操作结束时间 */	private Date operEndDate;	/** json数据格式，多表用，号分开 */	private String operData;	public void setOperData(String operData) {		this.operData = operData;	}	public String getOperData() {		return this.operData;	}	/** 日志类型 */	private String logType;	public void setLogType(String logType) {		this.logType = logType;	}	public String getLogType() {		return this.logType;	}	/** 操作方法 */	private String operMethod;	public void setOperMethod(String operMethod) {		this.operMethod = operMethod;	}	public String getOperMethod() {		return this.operMethod;	}	/** 操作异常 */	private String operException;	public void setOperException(String operException) {		this.operException = operException;	}	public String getOperException() {		return this.operException;	}	@JsonFormat(pattern="yyyy-MM-dd HH:mm:ss",timezone="GMT+8")	public Date getOperStartDate() {		return operStartDate;	}	public void setOperStartDate(Date operStartDate) {		this.operStartDate = operStartDate;	}	@JsonFormat(pattern="yyyy-MM-dd HH:mm:ss",timezone="GMT+8")	public Date getOperEndDate() {		return operEndDate;	}	public void setOperEndDate(Date operEndDate) {		this.operEndDate = operEndDate;	}}